tinymce.PluginManager.add('tectoro', function(editor, url) {
  var fields = editor.getParam("data")
  var menu = []
  _.each(fields,function(field){
    var key = "{{."+field.key+"}}"
    menu.push({"text":field.description,onclick: function() {editor.insertContent(key);}})
  })
  editor.addButton('schemabtn', {
        type: 'menubutton',
        text: 'Schema',
        icon: false,
        menu: menu
    });
})
